import { Component, inject, signal, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, Router, RouterModule } from '@angular/router';
import { ProductService } from '../../services/product.service';
import { Product } from '../../models/product.interface';

@Component({
  selector: 'app-product-detail',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.scss']
})
export class ProductDetailComponent implements OnInit {
  private readonly route = inject(ActivatedRoute);
  private readonly router = inject(Router);
  private readonly productService = inject(ProductService);

  // Signals for reactive state management
  product = signal<Product | null>(null);
  isLoading = signal(false);
  error = signal<string | null>(null);

  ngOnInit(): void {
    this.loadProduct();
  }

  loadProduct(): void {
    const productId = this.route.snapshot.paramMap.get('id');

    if (!productId) {
      this.error.set('Product ID is required');
      return;
    }

    this.isLoading.set(true);
    this.error.set(null);

    this.productService.getProductById(productId).subscribe({
      next: (product) => {
        this.product.set(product);
        this.isLoading.set(false);
      },
      error: (err) => {
        this.error.set('Failed to load product details. Please try again later.');
        this.isLoading.set(false);
        console.error('Error loading product:', err);
      }
    });
  }

  get stockStatus(): { text: string; class: string } {
    const product = this.product();
    if (!product) return { text: '', class: '' };

    if (product.quantity === 0) {
      return { text: 'Out of Stock', class: 'out-of-stock' };
    } else if (product.quantity < 10) {
      return { text: 'Low Stock', class: 'low-stock' };
    } else {
      return { text: 'In Stock', class: 'in-stock' };
    }
  }

  get formattedPrice(): string {
    const product = this.product();
    return product ? `$${product.price.toFixed(2)}` : '';
  }

  goBack(): void {
    this.router.navigate(['/products']);
  }
}
<div class="product-detail-container">
  <!-- Loading State -->
  <div *ngIf="isLoading()" class="loading-container">
    <div class="loading-spinner"></div>
    <p>Loading product details...</p>
  </div>

  <!-- Error State -->
  <div *ngIf="error()" class="error-container">
    <div class="error-icon">⚠️</div>
    <p class="error-message">{{ error() }}</p>
    <button class="retry-button" (click)="loadProduct()">
      Try Again
    </button>
  </div>

  <!-- Product Details -->
  <div *ngIf="!isLoading() && !error() && product()" class="product-detail-content">
    <div class="back-button-container">
      <button class="back-button" (click)="goBack()">
        ← Back to Products
      </button>
    </div>

    <div class="product-header">
      <h1 class="product-title">{{ product()?.name }}</h1>
      <span class="stock-status" [class]="stockStatus.class">
        {{ stockStatus.text }}
      </span>
    </div>

    <div class="product-info-grid">
      <div class="product-card">
        <div class="info-section">
          <h3>Product Information</h3>
          <div class="info-row">
            <span class="label">Product ID:</span>
            <span class="value">{{ product()?.id }}</span>
          </div>
          <div class="info-row">
            <span class="label">Name:</span>
            <span class="value">{{ product()?.name }}</span>
          </div>
          <div class="info-row">
            <span class="label">Price:</span>
            <span class="value price">{{ formattedPrice }}</span>
          </div>
          <div class="info-row">
            <span class="label">Quantity:</span>
            <span class="value quantity">{{ product()?.quantity }}</span>
          </div>
        </div>
      </div>

      <div class="product-card">
        <div class="info-section">
          <h3>Stock Status</h3>
          <div class="stock-indicator" [class]="stockStatus.class">
            <div class="indicator-dot"></div>
            <span>{{ stockStatus.text }}</span>
          </div>
          <p class="stock-description">
            <span *ngIf="product()?.quantity === 0">
              This product is currently out of stock.
            </span>
            <span *ngIf="product() && product()!.quantity > 0 && product()!.quantity < 10">
              Only {{ product()!.quantity }} items remaining in stock.
            </span>
            <span *ngIf="product() && product()!.quantity >= 10">
              This product is well stocked with {{ product()!.quantity }} items available.
            </span>
          </p>
        </div>
      </div>
    </div>
  </div>
</div>
