import { Component, inject, signal, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { ProductService } from '../../services/product.service';
import { Product } from '../../models/product.interface';
import { ProductCardComponent } from '../product-card/product-card.component';

@Component({
  selector: 'app-product-list',
  standalone: true,
  imports: [CommonModule, RouterModule, ProductCardComponent],
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.scss']
})
export class ProductListComponent implements OnInit {
  private readonly productService = inject(ProductService);

  // Signals for reactive state management
  products = signal<Product[]>([]);
  isLoading = signal(false);
  error = signal<string | null>(null);

  ngOnInit(): void {
    this.loadProducts();
  }

  loadProducts(): void {
    this.isLoading.set(true);
    this.error.set(null);

    this.productService.getAllProducts().subscribe({
      next: (products) => {
        this.products.set(products);
        this.isLoading.set(false);
      },
      error: (err) => {
        this.error.set('Failed to load products. Please try again later.');
        this.isLoading.set(false);
        console.error('Error loading products:', err);
      }
    });
  }

  trackByProductId(index: number, product: Product): string {
    return product.id;
  }
}
<div class="product-list-container">
  <header class="product-list-header">
    <h1>Product Inventory</h1>
    <p class="subtitle">Browse our complete product catalog</p>
  </header>

  <!-- Loading State -->
  <div *ngIf="isLoading()" class="loading-container">
    <div class="loading-spinner"></div>
    <p>Loading products...</p>
  </div>

  <!-- Error State -->
  <div *ngIf="error()" class="error-container">
    <div class="error-icon">‚ö†Ô∏è</div>
    <p class="error-message">{{ error() }}</p>
    <button class="retry-button" (click)="loadProducts()">
      Try Again
    </button>
  </div>

  <!-- Products Grid -->
  <div *ngIf="!isLoading() && !error() && products().length > 0" class="products-grid">
    <app-product-card
      *ngFor="let product of products(); trackBy: trackByProductId"
      [product]="product"
      class="product-card"
    ></app-product-card>
  </div>

  <!-- Empty State -->
  <div *ngIf="!isLoading() && !error() && products().length === 0" class="empty-state">
    <div class="empty-icon">üì¶</div>
    <h3>No Products Found</h3>
    <p>There are currently no products in the inventory.</p>
  </div>
</div>
